import React, { useEffect, useState } from 'react';
import img from '../assets/home-bg.jpeg';
import {  ScrollAnimation } from 'react-animated-css';


function Home() {
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    setIsLoaded(true);
  }, []);

  return (
    <div className="relative">
      <img src={img} alt="My Image" className="w-full h-auto opacity-80" />
      {isLoaded && (
        <ScrollAnimation animateIn="slideInUp" animateOnce={true}>
          <div className="absolute bottom-0 left-0 w-full h-full flex items-center justify-center">
            <div className="bg-white p-4 rounded-lg shadow">
              <h1 className="text-4xl font-bold">Title</h1>
              <p className="text-lg">Some text goes here.</p>
            </div>
          </div>
        </ScrollAnimation>
      )}
    </div>
  );
}

export default Home;
