import React, { useState, useEffect } from 'react';
import earthVid from '../assets/home-bg-video.mp4';

function Home() {
  const [isVideoLoaded, setIsVideoLoaded] = useState(false);

  useEffect(() => {
    const videoElement = document.getElementById('home-bg-video');
    videoElement.addEventListener('loadeddata', handleVideoLoad);
    return () => {
      videoElement.removeEventListener('loadeddata', handleVideoLoad);
    };
  }, []);

  const handleVideoLoad = () => {
    setIsVideoLoaded(true);
  };

  return (
    <div className="relative">
      <video
        id="home-bg-video"
        src={earthVid}
        alt="home bg logo"
        className="w-screen h-screen object-cover"
        autoPlay
        loop
        muted
      />
      {isVideoLoaded && (
        <div className="absolute bottom-0 left-0 w-full h-full flex items-center justify-center z-10">
          <h1 className="flex font-thin font-mono animate-bounce text-8xl text-white">Welcome</h1>
        </div>
      )}
    </div>
  );
}

export default Home;
