import React, { useState } from 'react';
import earthVid from '../assets/home-bg-video.mp4';

function Home() {
  const [isImageLoaded, setIsImageLoaded] = useState(false);

  const handleImageLoad = () => {
    setIsImageLoaded(true);
  };

  return (
    <div className="relative">
      <div className="absolute bottom-0 left-0 w-full h-full flex items-center justify-center z-10">
        {isImageLoaded && (
          <h1 className="flex font-thin font-mono animate-bounce text-8xl text-white">Welcome</h1>
        )}
      </div>
      <video
        src={earthVid}
        alt="home bg logo"
        className="w-screen h-screen"
        onLoad={handleImageLoad}
        autoPlay
        loop
        muted
      />
    </div>
  );
}

export default Home;
