import React, { useEffect, useState } from 'react';
import earthVid from '../assets/home-bg-video.mp4';


function Home() {
  const [isTextVisible, setIsTextVisible] = useState(false);
  const [typedText, setTypedText] = useState('');

  const textToType = 'Welcome';

  useEffect(() => {
    setIsTextVisible(true);
    typeText();
  }, []);

  const typeText = () => {
    let currentIndex = 0;
    const typingInterval = setInterval(() => {
      setTypedText((prevText) => prevText + textToType[currentIndex]);
      currentIndex++;
      if (currentIndex === textToType.length) {
        clearInterval(typingInterval);
      }
    }, 200);
  };

  return (
    <div className="relative">
      <video
        src={earthVid}
        alt="home bg logo"
        className="w-screen h-screen object-cover"
        autoPlay
        loop
        muted
      />
      {isTextVisible && (
        <div className="absolute bottom-0 left-0 w-full h-full flex items-center justify-center z-10">
          <h1 className="flex font-thin font-mono text-8xl text-white">Welcome</h1>
        </div>
      )}
    </div>
  );
}

export default Home;
